/*General */

html,
body {
  margin: 0px;
  padding: 0px;
}
.app {
  background-color: #f8f4e4;
  font-family: "Type";
}

.navbarbrand {
  font-family: "Cute";
}

.navlink {
  color: black;
  padding: 10px;
}
/* Login Page */

.is-invalid {
  border-color: red;
}
.login-page {
  background-color: #dde7cc;
  height: 100vh;
  width: 100vw;
  display: flex;
  align-items: center;
  flex-direction: column;
}

.login-page-logo {
  height: 70vh;
  margin-top: 10vh;
}

.login-page-logo img {
  max-height: 100%;
  max-width: 100%;
}

/* Main */

.app {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  height: 100vh;
}

.navbar {
  border: 5px double blue;
  border-bottom: 0px;
}

.main {
  display: flex;
  height: inherit;
  flex-direction: row;
}
.book-nav {
  border: 5px double blue;
  width: 250px;
  display: flex;
  flex-direction: column;
  text-align: center;
  align-items: center;
}

.pagination {
  margin-top: auto;
}
.content {
  border: 5px double blue;
  border-left: 0px;
  width: 100%;
  padding-top: 1vw;
  padding-left: 3vw;
}

/* Contact */

.contact-form {
  padding: 20px;
  margin: 10px;
  max-width: 700px;
}

.contact-input {
  margin-top: 10px;
}

/* Chat */
.chat-app {
  justify-content: space-around;
  display: flex;
}

.chat-wrapper {
  background: #f8f4e4;
  max-width: 60vw;
  border: 2px solid blue;
  /* position: relative; */
}

.chat-app form {
  display: grid;
  grid-template-columns: repeat(4, minmax(60px, 1fr));
  border: 3px soild blue;
  border-top: 3px solid blue;
}

.chat-app label {
  display: block;
  grid-column: 1 / -1;
}

.chat-app input {
  font-size: 1rem;
  padding: 5px 10px;
}

.chat-app input[type="text"] {
  color: black;
  border: 1px solid transparent;
  /* font-weight: bold; */
}

.chat-app input[type="submit"] {
  background-color: blue;
  box-shadow: none;
  color: white;
  text-align: center;
  border: 1px solid blue;
  text-transform: uppercase;
  grid-column: -2 / -1;
}

#nameInput,
#nameInput::placeholder {
  grid-column: 1 / 2;
  text-transform: uppercase;
  color: blue;
}

.chat-app #messageInput {
  grid-column: 2 / -2;
}

.chat-app input[type="text"]::placeholder {
  font-weight: regular;
}

.chat-app ul {
  list-style: none;
  padding: 0;
}

.chat-messages {
  height: 70vh;
  overflow: scroll;
  margin: 0px;
  margin-left: 2px;
}

.chat-messages-wrap {
  position: relative;
}

