.chat-app strong {
  text-transform: uppercase;
  color: #e44d48;
  grid-column: 1 / 2;
  align-self: center;
}

.chat-app p {
  grid-column: 2 / -2;
}

.chat-app li button {
  background: none;
  border: none;
  cursor: pointer;
  grid-column-end: -1;
  justify-self: end;
}

@media (max-width: 820px) {
  .chat-app #nameInput,
  .chat-app #messageInput,
  input[type="submit"] {
    grid-column: 1/ -1;
  }
}

@media (max-width: 530px) {
  .chat-app strong,
  .chat-app p,
  .chat-app li button {
    grid-column: 1 / -1;
  }
}

@media (max-width: 325px) {
  .chat-app ul li {
    grid-template-columns: minmax(65px, 1fr);
  }
  .chat-app form {
    grid-template-columns: minmax(60px, 1fr);
  }
  .chat-app h1 {
    font-size: 3rem;
  }
  .chat-app h2 {
    font-size: 1.8rem;
  }
  .chat-app .title {
    bottom: 20px;
  }
}
/*mobile styles*/

@media only screen and (max-width: 770px) {
  .book-nav {
    width: 100%;
  }
  .content {
    width: 0px;
    border: 0px;
    display: none;
    overflow: hidden;
  }
}
